def is_symbol_supported(symbol: str, usdt_only: bool = True) -> bool:
    """Check if a symbol is supported for futures trading."""
    symbol = symbol.upper()
    if usdt_only:
        return symbol in USDT_FUTURES_SYMBOLS
    return symbol in ALL_FUTURES_SYMBOLS

def format_symbol_for_binance(pair: str) -> str:
    """Convert pair format to Binance format (e.g., eth_usdt -> ETHUSDT)."""
    return pair.replace("_", "").upper()
